mapper.update:
UPDATE nodes
SET
    status = #{status},
    update_at = #{update_at}
WHERE
    node_id = #{node_id}

UPDATE nodes
SET
    node_name = #{node_name},
    nanodc_id = #{nanodc_id}
WHERE
    node_id = #{node_id}

mapper.select:
SELECT COUNT(1)
FROM nodes
WHERE node_id = #{node_id}

SELECT nanodc_id
FROM nodes
WHERE node_id = #{node_id}

SELECT status
FROM nodes
WHERE node_id = #{node_id}
